import { CHANGE_SEARCH_FIELD } from './constants';

const initialStateSearch = {
	searchField: ''
}

export const searchRobots = (state = initialStateSearch , action = {}) => {
	switch(action.type){
		case CHANGE_SEARCH_FIELD:
			return Object.assign({}, state, { searchFiled: action.payload });
		default: 
			return state	
	}
}
